 % MNItoFSAVERAGE.mfunction MNItoFSAVERAGE(NIFTI,OUTPATH)% Run the following below first:% setenv('PATH',['/Applications/workbench/bin_macosx64:',getenv('PATH')])% Add the gifti toolbox to your path e.g.:% addpath('/Users/kevinaquino/projects/gifti')CBIG_DIR='/Users/alexfornito/Alex_Docs/Manuscripts-Publications/In_Prep/Sara-PLS_of_PLEs/SurfaceMapping//KevinMapping/CBIG/'CBIG_SH=[CBIG_DIR,'stable_projects/registration/Wu2017_RegistrationFusion/bin/standalone_scripts_for_MNI_fsaverage_projection/CBIG_RF_projectMNI2fsaverage.sh'];% Change this to whereever your matlab bin dir isMATLAB_DIR='/Applications/MATLAB_R2019a.app/bin/'disp('RUNNING CBIG DIR');% keyboardsystem(['sh ',CBIG_SH,' -m ',MATLAB_DIR,' -s ',NIFTI,' -o ',OUTPATH,'/ -f /Applications/freesurfer/']);% GIFTI setup:gifti_tmp=gifti;gifti_tmp2=gifti;% Get the file parts[~,f,~]=fileparts(NIFTI);[~,f,~]=fileparts(f);left=MRIread([OUTPATH,'/lh.',f,'.allSub_RF_ANTs_MNI152_orig_to_fsaverage.nii.gz']);right=MRIread([OUTPATH,'/rh.',f,'.allSub_RF_ANTs_MNI152_orig_to_fsaverage.nii.gz']);gifti_tmp.cdata=[left.vol'];gifti_tmp2.cdata=[right.vol'];save(gifti_tmp,[OUTPATH,'/lh_',f,'.func.gii']);save(gifti_tmp2,[OUTPATH,'/rh_',f,'.func.gii']);